// array of messages
const messages = [
  {role: 'human', text: 'Hello, how are you?'},
  {role: 'bot', text: 'Hello! I am a fake chatbot. I am doing well, thank you.'},
  {role: 'human', text: 'That is good to hear. What can you do as a chatbot?'},
