#include <iostream>
#include <regex>
#include <string>

std::string markdownToHtml(const std::string& markdown) {
    std::string html = markdown;

    // Convert Markdown headings to HTML headings
    for (int i = 6; i >= 1; --i) {
        std::string mdHeading(i, '#');
        std::regex headingRegex("^" + mdHeading + " (.*)", std::regex_constants::multiline);
        std::string replaceStr = "<h" + std::to_string(i) + ">$1</h" + std::to_string(i) + ">";
        html = std::regex_replace(html, headingRegex, replaceStr);
    }

    // Convert bold text
    html = std::regex_replace(html, std::regex("\\*\\*(.*)\\*\\*"), "<strong>$1</strong>");
    // Convert italic text
